import React from 'react';
import {
  Grid,
  Paper,
  Typography,
  Box,
  Card,
  CardContent,
  CardHeader,
} from '@mui/material';
import {
  Apps as AppsIcon,
  Storage as StorageIcon,
  Security as SecurityIcon,
  Monitoring as MonitoringIcon,
} from '@mui/icons-material';

const Dashboard = () => {
  const summaryCards = [
    {
      title: 'Applications',
      count: '5',
      icon: <AppsIcon sx={{ fontSize: 40, color: '#1976d2' }} />,
      description: 'Active applications',
    },
    {
      title: 'Infrastructure',
      count: '3',
      icon: <StorageIcon sx={{ fontSize: 40, color: '#2e7d32' }} />,
      description: 'Running services',
    },
    {
      title: 'Security',
      count: '98%',
      icon: <SecurityIcon sx={{ fontSize: 40, color: '#ed6c02' }} />,
      description: 'Security score',
    },
    {
      title: 'Monitoring',
      count: '100%',
      icon: <MonitoringIcon sx={{ fontSize: 40, color: '#9c27b0' }} />,
      description: 'System uptime',
    },
  ];

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Welcome to DevOps Forge
      </Typography>
      <Typography variant="subtitle1" color="textSecondary" paragraph>
        Your cloud-native application management platform
      </Typography>

      <Grid container spacing={3} sx={{ mt: 2 }}>
        {summaryCards.map((card) => (
          <Grid item xs={12} sm={6} md={3} key={card.title}>
            <Card
              sx={{
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                '&:hover': {
                  boxShadow: 6,
                  transform: 'scale(1.02)',
                  transition: 'all 0.2s ease-in-out',
                },
              }}
            >
              <CardContent>
                <Box
                  sx={{
                    display: 'flex',
                    alignItems: 'center',
                    mb: 2,
                  }}
                >
                  {card.icon}
                  <Typography
                    variant="h4"
                    component="div"
                    sx={{ ml: 2, fontWeight: 'bold' }}
                  >
                    {card.count}
                  </Typography>
                </Box>
                <Typography variant="h6" component="div">
                  {card.title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {card.description}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Grid container spacing={3} sx={{ mt: 3 }}>
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              Recent Activity
            </Typography>
            <Typography variant="body2" color="text.secondary">
              No recent activity to display.
            </Typography>
          </Paper>
        </Grid>
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              System Status
            </Typography>
            <Typography variant="body2" color="text.secondary">
              All systems are operating normally.
            </Typography>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Dashboard;
